import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
  Box,
  Container,
  Grid,
  Typography,
  Link,
  IconButton,
  Divider,
  useTheme,
  useMediaQuery
} from '@mui/material';
import FacebookIcon from '@mui/icons-material/Facebook';
import TwitterIcon from '@mui/icons-material/Twitter';
import InstagramIcon from '@mui/icons-material/Instagram';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import EventAvailableIcon from '@mui/icons-material/EventAvailable';

const Footer = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  
  const currentYear = new Date().getFullYear();
  
  const socialLinks = [
    { icon: <FacebookIcon />, url: 'https://facebook.com', name: 'Facebook' },
    { icon: <TwitterIcon />, url: 'https://twitter.com', name: 'Twitter' },
    { icon: <InstagramIcon />, url: 'https://instagram.com', name: 'Instagram' },
    { icon: <LinkedInIcon />, url: 'https://linkedin.com', name: 'LinkedIn' }
  ];
  
  const footerLinks = [
    { title: 'Company', links: [
      { name: 'About Us', url: '/about' },
      { name: 'Contact', url: '/contact' },
      { name: 'Careers', url: '/careers' }
    ]},
    { title: 'Resources', links: [
      { name: 'Blog', url: '/blog' },
      { name: 'Help Center', url: '/help' },
      { name: 'FAQs', url: '/faqs' }
    ]},
    { title: 'Legal', links: [
      { name: 'Privacy Policy', url: '/privacy' },
      { name: 'Terms of Service', url: '/terms' },
      { name: 'Cookie Policy', url: '/cookies' }
    ]}
  ];
  
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.2
      }
    }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };
  
  const socialIconVariants = {
    hover: {
      y: -5,
      scale: 1.1,
      transition: { duration: 0.2 }
    },
    tap: { scale: 0.9 }
  };
  
  const linkVariants = {
    hover: {
      x: 5,
      color: theme.palette.primary.main,
      transition: { duration: 0.2 }
    }
  };
  
  return (
    <Box
      sx={{
        bgcolor: 'white',
        color: 'text.primary',
        py: 6,
        borderTop: '1px solid rgba(0, 0, 0, 0.05)',
        boxShadow: '0 -4px 20px rgba(0, 0, 0, 0.03)'
      }}
      component={motion.footer}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          {/* Logo and Description */}
          <Grid item xs={12} md={4}>
            <motion.div variants={itemVariants}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <motion.div
                  whileHover={{ rotate: 10, scale: 1.1 }}
                  transition={{ duration: 0.2 }}
                >
                  <EventAvailableIcon 
                    sx={{ 
                      fontSize: '2rem', 
                      color: 'primary.main', 
                      mr: 1 
                    }} 
                    className="floating-icon"
                  />
                </motion.div>
                <Typography 
                  variant="h6" 
                  component="div" 
                  sx={{ fontWeight: 700 }}
                >
                  Appointment Scheduler
                </Typography>
              </Box>
              <Typography variant="body2" color="text.secondary" paragraph>
                Streamline your appointment booking process. Easy to use for both businesses and customers.
                Book appointments, manage your schedule, and receive reminders all in one place.
              </Typography>
              
              {/* Social Media Links */}
              <Box sx={{ mt: 3, display: 'flex' }}>
                {socialLinks.map((social, index) => (
                  <motion.div
                    key={index}
                    variants={socialIconVariants}
                    whileHover="hover"
                    whileTap="tap"
                  >
                    <IconButton 
                      component="a" 
                      href={social.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      aria-label={social.name}
                      sx={{
                        color: 'primary.main',
                        mr: 1,
                        bgcolor: 'rgba(58, 111, 247, 0.08)',
                        '&:hover': {
                          bgcolor: 'rgba(58, 111, 247, 0.15)'
                        }
                      }}
                    >
                      {social.icon}
                    </IconButton>
                  </motion.div>
                ))}
              </Box>
            </motion.div>
          </Grid>
          
          {/* Footer Links */}
          {!isMobile && footerLinks.map((section, index) => (
            <Grid item xs={12} sm={4} md={2} key={index}>
              <motion.div variants={itemVariants}>
                <Typography 
                  variant="subtitle1" 
                  color="text.primary" 
                  gutterBottom
                  sx={{ fontWeight: 600 }}
                >
                  {section.title}
                </Typography>
                <Box component="ul" sx={{ listStyle: 'none', p: 0, m: 0 }}>
                  {section.links.map((link, linkIndex) => (
                    <Box component="li" key={linkIndex} sx={{ mb: 1 }}>
                      <motion.div
                        variants={linkVariants}
                        whileHover="hover"
                      >
                        <Link
                          component={RouterLink}
                          to={link.url}
                          color="text.secondary"
                          underline="hover"
                          sx={{
                            display: 'inline-block',
                            fontSize: '0.9rem',
                            transition: 'color 0.2s ease'
                          }}
                        >
                          {link.name}
                        </Link>
                      </motion.div>
                    </Box>
                  ))}
                </Box>
              </motion.div>
            </Grid>
          ))}
          
          {/* Newsletter Subscription - Mobile View */}
          {isMobile && (
            <Grid item xs={12} sx={{ mt: 3 }}>
              <motion.div variants={itemVariants}>
                <Typography 
                  variant="subtitle1" 
                  gutterBottom 
                  sx={{ fontWeight: 600 }}
                >
                  Quick Links
                </Typography>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
                  {footerLinks.flatMap(section => 
                    section.links.map((link, i) => (
                      <motion.div
                        key={`${section.title}-${i}`}
                        variants={linkVariants}
                        whileHover="hover"
                      >
                        <Link
                          component={RouterLink}
                          to={link.url}
                          color="text.secondary"
                          underline="hover"
                          sx={{ fontSize: '0.9rem' }}
                        >
                          {link.name}
                        </Link>
                      </motion.div>
                    ))
                  )}
                </Box>
              </motion.div>
            </Grid>
          )}
          
          {/* Newsletter Subscription - Desktop View */}
          {!isMobile && (
            <Grid item xs={12} md={4}>
              <motion.div variants={itemVariants}>
                <Typography 
                  variant="subtitle1" 
                  gutterBottom
                  sx={{ fontWeight: 600 }}
                >
                  Subscribe to our Newsletter
                </Typography>
                <Typography variant="body2" color="text.secondary" paragraph>
                  Stay updated with the latest features and announcements.
                </Typography>
                <Box
                  component="form"
                  sx={{
                    display: 'flex',
                    alignItems: 'center',
                    '& .MuiOutlinedInput-root': {
                      mr: 1,
                      borderRadius: '8px',
                      transition: 'transform 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-2px)'
                      }
                    }
                  }}
                >
                  <motion.input
                    type="email"
                    placeholder="Your email address"
                    whileFocus={{ scale: 1.02 }}
                    style={{
                      flex: 1,
                      padding: '10px 16px',
                      border: '2px solid rgba(0, 0, 0, 0.1)',
                      borderRadius: '8px',
                      outline: 'none',
                      fontFamily: 'inherit',
                      fontSize: '0.9rem',
                      marginRight: '8px'
                    }}
                  />
                  <motion.button
                    whileHover={{ scale: 1.05, backgroundColor: theme.palette.primary.dark }}
                    whileTap={{ scale: 0.95 }}
                    style={{
                      backgroundColor: theme.palette.primary.main,
                      color: 'white',
                      border: 'none',
                      padding: '11px 20px',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      fontWeight: 600,
                      fontSize: '0.9rem',
                      boxShadow: '0 4px 14px rgba(58, 111, 247, 0.25)'
                    }}
                  >
                    Subscribe
                  </motion.button>
                </Box>
              </motion.div>
            </Grid>
          )}
        </Grid>
        
        <Divider sx={{ my: 4 }} />
        
        {/* Copyright */}
        <Box
          sx={{
            display: 'flex',
            flexDirection: { xs: 'column', sm: 'row' },
            justifyContent: 'space-between',
            alignItems: { xs: 'center', sm: 'flex-start' },
            textAlign: { xs: 'center', sm: 'left' }
          }}
        >
          <motion.div variants={itemVariants}>
            <Typography variant="body2" color="text.secondary">
              Â© {currentYear} Appointment Scheduler. All rights reserved.
            </Typography>
          </motion.div>
          
          <motion.div variants={itemVariants}>
            <Box
              sx={{
                display: 'flex',
                gap: 3,
                mt: { xs: 2, sm: 0 }
              }}
            >
              <Link
                component={RouterLink}
                to="/privacy"
                color="text.secondary"
                underline="hover"
                variant="body2"
              >
                Privacy Policy
              </Link>
              <Link
                component={RouterLink}
                to="/terms"
                color="text.secondary"
                underline="hover"
                variant="body2"
              >
                Terms of Service
              </Link>
              <Link
                component={RouterLink}
                to="/cookies"
                color="text.secondary"
                underline="hover"
                variant="body2"
              >
                Cookies
              </Link>
            </Box>
          </motion.div>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
