import BusinessIcon from '@mui/icons-material/Business';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import VisibilityIcon from '@mui/icons-material/Visibility';
import { motion } from 'framer-motion';

// Framer Motion animations
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1
    }
  }
};

const itemVariants = {
  hidden: { y: 20, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      type: 'spring',
      stiffness: 100
    }
  }
};

const Dashboard = () => {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showNotificationBanner, setShowNotificationBanner] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await api.get('/api/appointments');
        setAppointments(res.data);

        // Schedule notifications for upcoming appointments
        if (res.data && res.data.length > 0) {
          notificationService.scheduleAppointmentNotifications(res.data);
        }

        setError('');
      } catch (error) {
        console.error('Error fetching appointments:', error);
        setError('Failed to load appointments');
        toast.error('Failed to load appointments');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchAppointments();

      // Check if we should show notification permission banner
      if (notificationService.hasSupport && notificationService.permission !== 'granted') {
        setShowNotificationBanner(true);
      }
    }

    // Cleanup notifications when component unmounts
    return () => {
      notificationService.clearAllNotifications();
    };
  }, [user]);

  const handleCancelAppointment = async (id) => {
    try {
      await api.delete(`/api/appointments/${id}`);

      // Update appointments state
      const updatedAppointments = appointments.map(appointment => 
        appointment._id === id 
          ? { ...appointment, status: 'cancelled' } 
          : appointment
      );

      setAppointments(updatedAppointments);

      // Re-schedule notifications with updated appointments
      notificationService.scheduleAppointmentNotifications(updatedAppointments);

      toast.success('Appointment cancelled successfully');
    } catch (error) {
      console.error('Error cancelling appointment:', error);
      toast.error('Failed to cancel appointment');
    }
  };

  const handleEnableNotifications = async () => {
    const granted = await notificationService.requestPermission();
    if (granted) {
      setShowNotificationBanner(false);
      notificationService.scheduleAppointmentNotifications(appointments);
      toast.success('Notifications enabled successfully');
    } else {
      toast.error('Please allow notifications in your browser settings');
    }
  };

  // Get status color for chip
  const getStatusColor = (status) => {
    switch (status) {
      case 'scheduled':
        return 'primary';
      case 'completed':
        return 'success';
      case 'cancelled':
        return 'error';
      case 'rescheduled':
        return 'warning';
      default:
        return 'default';
    }
  };

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress size={60} thickness={4} />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 2, mb: 8 }}>
      <Snackbar
        open={showNotificationBanner}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert 
          severity="info" 
          action={
            <Button 
              color="inherit" 
              size="small" 
              startIcon={<NotificationsIcon />}
              onClick={handleEnableNotifications}
            >
              Enable
            </Button>
          }
          onClose={() => setShowNotificationBanner(false)}
        >
          Enable notifications to receive appointment reminders
        </Alert>
      </Snackbar>

      <Fade in={true} timeout={800}>
        <Box>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            mb: 4,
            flexDirection: isMobile ? 'column' : 'row',
            gap: isMobile ? 2 : 0
          }}>
            <Box>
              <Typography 
                variant="h4" 
                component="h1" 
                sx={{ 
                  fontWeight: 700,
                  background: 'linear-gradient(45deg, #3a6ff7 30%, #5e9cff 90%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                }}
              >
                Welcome, {user?.name}!
              </Typography>
              <Typography variant="subtitle1" color="text.secondary" sx={{ mt: 1 }}>
                Here's an overview of your appointments
              </Typography>
            </Box>
            <Box sx={{ 
              display: 'flex', 
              gap: 2,
              width: isMobile ? '100%' : 'auto'
            }}>
              <Button
                variant="contained"
                color="primary"
                startIcon={<AddIcon />}
                component={RouterLink}
                to="/appointments/new"
                fullWidth={isMobile}
                sx={{ 
                  boxShadow: '0 4px 14px 0 rgba(58, 111, 247, 0.39)',
                }}
              >
                Book Appointment
              </Button>
              <Button
                variant="outlined"
                startIcon={<CalendarMonthIcon />}
                component={RouterLink}
                to="/calendar"
                fullWidth={isMobile}
              >
                Calendar View
              </Button>
            </Box>
          </Box>

          <Paper 
            elevation={0}
            sx={{
              p: 3,
              mb: 4,
              borderRadius: '16px',
              background: 'linear-gradient(135deg, rgba(94, 53, 177, 0.05) 0%, rgba(58, 111, 247, 0.05) 100%)',
              border: '1px solid rgba(58, 111, 247, 0.1)',
            }}
          >
            <Typography variant="h5" sx={{ mb: 2, fontWeight: 600 }}>
              My Appointments
            </Typography>

            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}

            {appointments.length === 0 ? (
              <Card 
                sx={{ 
                  mb: 4, 
                  p: 2, 
                  textAlign: 'center',
                  borderRadius: '16px',
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  border: '1px solid rgba(255, 255, 255, 0.18)',
                }}
                className="glass-effect"
              >
                <CardContent>
                  <Box sx={{ p: 4 }}>
                    <motion.div
                      initial={{ scale: 0.8, opacity: 0 }}
                      animate={{ scale: 1, opacity: 1 }}
                      transition={{ duration: 0.5 }}
                    >
                      <CalendarMonthIcon sx={{ fontSize: 80, color: 'primary.main', opacity: 0.7, mb: 2 }} />
                    </motion.div>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      You don't have any appointments yet
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      Book your first appointment to get started
                    </Typography>
                    <Button
                      variant="contained"
                      component={RouterLink}
                      to="/appointments/new"
                      startIcon={<AddIcon />}
                      size="large"
                      className="gradient-primary"
                    >
                      Book Your First Appointment
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            ) : (
              <motion.div
                variants={containerVariants}
                initial="hidden"
                animate="visible"
              >
                <Grid container spacing={3}>
                  {appointments.map((appointment, index) => (
                    <Grid item xs={12} sm={6} md={4} key={appointment._id}>
                      <motion.div variants={itemVariants}>
                        <Card 
                          sx={{ 
                            height: '100%', 
                            display: 'flex', 
                            flexDirection: 'column',
                            position: 'relative',
                            overflow: 'visible',
                          }}
                          className="card-3d"
                        >
                          {appointment.status === 'scheduled' && (
                            <Box 
                              sx={{ 
                                position: 'absolute', 
                                top: -10, 
                                right: -10, 
                                bgcolor: 'primary.main',
                                color: 'white',
                                width: 30,
                                height: 30,
                                borderRadius: '50%',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                                zIndex: 1,
                              }}
                            >
                              {index + 1}
                            </Box>
                          )}
                          <CardContent sx={{ flexGrow: 1, p: 3 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2, alignItems: 'flex-start' }}>
                              <Typography 
                                variant="h6" 
                                component="div" 
                                sx={{ 
                                  fontWeight: 600,
                                  color: appointment.status === 'cancelled' ? 'text.disabled' : 'text.primary'
                                }}
                              >
                                {appointment.service?.name || 'Service'}
                              </Typography>
                              <Chip
                                label={appointment.status}
                                color={getStatusColor(appointment.status)}
                                size="small"
                                sx={{ fontWeight: 500 }}
                              />
                            </Box>

                            <Box sx={{ 
                              display: 'flex', 
                              flexDirection: 'column', 
                              gap: 1.5,
                              mb: 2,
                              opacity: appointment.status === 'cancelled' ? 0.7 : 1
                            }}>
                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <EventIcon fontSize="small" sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body2">
                                  {formatDate(appointment.date)}
                                </Typography>
                              </Box>

                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <AccessTimeIcon fontSize="small" sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body2">
                                  {appointment.startTime} - {appointment.endTime}
                                </Typography>
                              </Box>

                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <BusinessIcon fontSize="small" sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body2" noWrap>
                                  {appointment.business?.name || 'Provider'}
                                </Typography>
                              </Box>

                              {appointment.business?.address && (
                                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                  <LocationOnIcon fontSize="small" sx={{ mr: 1, color: 'primary.main' }} />
                                  <Typography variant="body2" noWrap>
                                    {appointment.business.address}
                                  </Typography>
                                </Box>
                              )}
                            </Box>

                            {appointment.notes && (
                              <Typography 
                                variant="body2" 
                                color="text.secondary" 
                                sx={{ 
                                  mt: 1, 
                                  p: 1.5, 
                                  bgcolor: 'background.paper', 
                                  borderRadius: 1,
                                  borderLeft: '3px solid',
                                  borderColor: 'primary.main',
                                  fontSize: '0.8rem'
                                }}
                              >
                                {appointment.notes}
                              </Typography>
                            )}
                          </CardContent>

                          <Divider />

                          <CardActions sx={{ justifyContent: 'space-between', p: 2 }}>
                            <Button
                              size="small"
                              component={RouterLink}
                              to={`/appointments/${appointment._id}`}
                              startIcon={<VisibilityIcon fontSize="small" />}
                              color="primary"
                            >
                              Details
                            </Button>

                            {appointment.status === 'scheduled' && (
                              <Button
                                size="small"
                                color="error"
                                onClick={() => handleCancelAppointment(appointment._id)}
                              >
                                Cancel
                              </Button>
                            )}
                          </CardActions>
                        </Card>
                      </motion.div>
                    </Grid>
                  ))}
                </Grid>
              </motion.div>
            )}
          </Paper>
        </Box>
      </Fade>
    </Container>
  );
};

export default Dashboard;
