import React, { useState } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { motion } from 'framer-motion';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Menu,
  MenuItem,
  Avatar,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  useMediaQuery,
  useTheme,
  Container,
  Tooltip
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import EventAvailableIcon from '@mui/icons-material/EventAvailable';
import DashboardIcon from '@mui/icons-material/Dashboard';
import BusinessIcon from '@mui/icons-material/Business';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import LoginIcon from '@mui/icons-material/Login';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import HomeIcon from '@mui/icons-material/Home';
import AccessTimeIcon from '@mui/icons-material/AccessTime';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  
  const [anchorEl, setAnchorEl] = useState(null);
  const [mobileOpen, setMobileOpen] = useState(false);
  
  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };
  
  const handleMenuClose = () => {
    setAnchorEl(null);
  };
  
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  
  const handleLogout = () => {
    logout();
    handleMenuClose();
    navigate('/login');
  };
  
  const handleNavigation = (path) => {
    navigate(path);
    handleMenuClose();
    setMobileOpen(false);
  };
  
  const isMenuOpen = Boolean(anchorEl);
  
  const menuId = 'primary-account-menu';
  
  // Animation variants
  const logoVariants = {
    hover: {
      scale: 1.05,
      rotate: 2,
      transition: { duration: 0.3 }
    }
  };
  
  const navItemVariants = {
    hover: {
      y: -3,
      transition: { duration: 0.2 }
    },
    tap: {
      scale: 0.95
    }
  };

  const drawer = (
    <Box onClick={handleDrawerToggle} sx={{ width: 250, pt: 2 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
        <motion.div
          whileHover={{ scale: 1.05, rotate: 2 }}
          whileTap={{ scale: 0.95 }}
        >
          <EventAvailableIcon sx={{ fontSize: 40, color: 'primary.main' }} />
        </motion.div>
      </Box>
      <Typography variant="h6" align="center" sx={{ mb: 2 }}>
        Appointment Scheduler
      </Typography>
      <Divider />
      <List>
        <ListItem button onClick={() => handleNavigation('/')}>
          <ListItemIcon>
            <HomeIcon color="primary" />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        
        {user ? (
          <>
            <ListItem button onClick={() => handleNavigation('/dashboard')}>
              <ListItemIcon>
                <DashboardIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Dashboard" />
            </ListItem>
            
            <ListItem button onClick={() => handleNavigation('/appointments/new')}>
              <ListItemIcon>
                <AddCircleOutlineIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Book Appointment" />
            </ListItem>
            
            <ListItem button onClick={() => handleNavigation('/calendar')}>
              <ListItemIcon>
                <CalendarMonthIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Calendar" />
            </ListItem>
            
            {user.role === 'business' && (
              <>
                <Divider />
                <ListItem button onClick={() => handleNavigation('/business/dashboard')}>
                  <ListItemIcon>
                    <BusinessIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText primary="Business Dashboard" />
                </ListItem>
                
                <ListItem button onClick={() => handleNavigation('/business/availability')}>
                  <ListItemIcon>
                    <AccessTimeIcon color="primary" />
                  </ListItemIcon>
                  <ListItemText primary="Manage Availability" />
                </ListItem>
              </>
            )}
            
            <Divider />
            <ListItem button onClick={() => handleNavigation('/profile')}>
              <ListItemIcon>
                <AccountCircleIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Profile" />
            </ListItem>
            
            <ListItem button onClick={handleLogout}>
              <ListItemIcon>
                <LogoutIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Logout" />
            </ListItem>
          </>
        ) : (
          <>
            <ListItem button onClick={() => handleNavigation('/login')}>
              <ListItemIcon>
                <LoginIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Login" />
            </ListItem>
            
            <ListItem button onClick={() => handleNavigation('/register')}>
              <ListItemIcon>
                <PersonAddIcon color="primary" />
              </ListItemIcon>
              <ListItemText primary="Register" />
            </ListItem>
          </>
        )}
      </List>
    </Box>
  );
  
  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar 
        position="static" 
        elevation={0}
        sx={{
          bgcolor: 'white',
          borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)'
        }}
      >
        <Container maxWidth="lg">
          <Toolbar>
            {isMobile && (
              <IconButton
                color="primary"
                edge="start"
                onClick={handleDrawerToggle}
                sx={{ mr: 2 }}
              >
                <MenuIcon />
              </IconButton>
            )}
            
            <motion.div
              component={RouterLink}
              to="/"
              variants={logoVariants}
              whileHover="hover"
              style={{ 
                display: 'flex', 
                alignItems: 'center', 
                textDecoration: 'none',
                color: 'inherit'
              }}
            >
              <EventAvailableIcon 
                sx={{ 
                  display: 'flex', 
                  mr: 1, 
                  color: 'primary.main',
                  fontSize: '2rem'
                }} 
                className="floating-icon"
              />
              <Typography
                variant="h6"
                noWrap
                component="div"
                sx={{ 
                  display: { xs: 'none', sm: 'block' },
                  fontWeight: 700,
                  color: 'text.primary'
                }}
              >
                Appointment Scheduler
              </Typography>
            </motion.div>
            
            <Box sx={{ flexGrow: 1 }} />
            
            {!isMobile && (
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <motion.div variants={navItemVariants} whileHover="hover" whileTap="tap">
                  <Button 
                    component={RouterLink} 
                    to="/" 
                    color="inherit"
                    sx={{ 
                      mx: 1,
                      color: 'text.primary',
                      fontWeight: 500
                    }}
                  >
                    Home
                  </Button>
                </motion.div>
                
                {user ? (
                  <>
                    <motion.div variants={navItemVariants} whileHover="hover" whileTap="tap">
                      <Button 
                        component={RouterLink} 
                        to="/dashboard" 
                        color="inherit"
                        sx={{ 
                          mx: 1,
                          color: 'text.primary',
                          fontWeight: 500
                        }}
                      >
                        Dashboard
                      </Button>
                    </motion.div>
                    
                    <motion.div variants={navItemVariants} whileHover="hover" whileTap="tap">
                      <Button 
                        component={RouterLink} 
                        to="/appointments/new" 
                        color="inherit"
                        sx={{ 
                          mx: 1,
                          color: 'text.primary',
                          fontWeight: 500
                        }}
                      >
                        Book
                      </Button>
                    </motion.div>
                    
                    {user.role === 'business' && (
                      <motion.div variants={navItemVariants} whileHover="hover" whileTap="tap">
                        <Button 
                          component={RouterLink} 
                          to="/business/dashboard" 
                          color="inherit"
                          sx={{ 
                            mx: 1,
                            color: 'text.primary',
                            fontWeight: 500
                          }}
                        >
                          Business
                        </Button>
                      </motion.div>
                    )}
                    
                    <Tooltip title="Account settings">
                      <motion.div
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        <IconButton
                          size="large"
                          edge="end"
                          aria-label="account of current user"
                          aria-controls={menuId}
                          aria-haspopup="true"
                          onClick={handleProfileMenuOpen}
                          color="inherit"
                          sx={{ ml: 1 }}
                        >
                          <Avatar 
                            sx={{ 
                              width: 35, 
                              height: 35,
                              bgcolor: 'primary.main',
                              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)'
                            }}
                          >
                            {user.name ? user.name.charAt(0).toUpperCase() : 'U'}
                          </Avatar>
                        </IconButton>
                      </motion.div>
                    </Tooltip>
                  </>
                ) : (
                  <>
                    <motion.div variants={navItemVariants} whileHover="hover" whileTap="tap">
                      <Button 
                        component={RouterLink} 
                        to="/login" 
                        variant="outlined" 
                        color="primary"
                        sx={{ 
                          mx: 1,
                          borderRadius: '8px',
                          borderWidth: '2px',
                          fontWeight: 600
                        }}
                      >
                        Login
                      </Button>
                    </motion.div>
                    
                    <motion.div 
                      variants={navItemVariants} 
                      whileHover="hover" 
                      whileTap="tap"
                      style={{ marginLeft: '8px' }}
                    >
                      <Button 
                        component={RouterLink} 
                        to="/register" 
                        variant="contained" 
                        color="primary"
                        sx={{ 
                          borderRadius: '8px',
                          boxShadow: '0 4px 14px rgba(58, 111, 247, 0.25)',
                          fontWeight: 600
                        }}
                      >
                        Sign Up
                      </Button>
                    </motion.div>
                  </>
                )}
              </Box>
            )}
          </Toolbar>
        </Container>
      </AppBar>
      
      <Menu
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'right',
        }}
        id={menuId}
        keepMounted
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        open={isMenuOpen}
        onClose={handleMenuClose}
        PaperProps={{
          sx: { 
            mt: 1.5,
            borderRadius: '12px',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)'
          }
        }}
      >
        <MenuItem onClick={() => handleNavigation('/profile')}>
          <ListItemIcon>
            <AccountCircleIcon fontSize="small" color="primary" />
          </ListItemIcon>
          <ListItemText primary="Profile" />
        </MenuItem>
        
        <MenuItem onClick={() => handleNavigation('/calendar')}>
          <ListItemIcon>
            <CalendarMonthIcon fontSize="small" color="primary" />
          </ListItemIcon>
          <ListItemText primary="Calendar" />
        </MenuItem>
        
        <Divider />
        
        <MenuItem onClick={handleLogout}>
          <ListItemIcon>
            <LogoutIcon fontSize="small" color="primary" />
          </ListItemIcon>
          <ListItemText primary="Logout" />
        </MenuItem>
      </Menu>
      
      <Drawer
        variant="temporary"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{ keepMounted: true }}
        sx={{
          display: { xs: 'block', md: 'none' },
          '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 250 },
        }}
      >
        {drawer}
      </Drawer>
    </Box>
  );
};

export default Navbar;
