import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import {
  AppBar,
  Box,
  Toolbar,
  IconButton,
  Typography,
  Menu,
  Container,
  Avatar,
  Button,
  Tooltip,
  MenuItem,
  useScrollTrigger,
  Slide,
  Fade
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import EventNoteIcon from '@mui/icons-material/EventNote';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import DashboardIcon from '@mui/icons-material/Dashboard';
import SettingsIcon from '@mui/icons-material/Settings';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import HomeIcon from '@mui/icons-material/Home';

// Hide navbar on scroll down
function HideOnScroll(props) {
  const { children } = props;
  const trigger = useScrollTrigger();

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const Navbar = (props) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [anchorElNav, setAnchorElNav] = useState(null);
  const [anchorElUser, setAnchorElUser] = useState(null);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
  }, []);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };

  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  const handleLogout = () => {
    logout();
    navigate('/');
    handleCloseUserMenu();
  };

  // Navigation links based on user role
  const getNavLinks = () => {
    if (!user) {
      return [
        { title: 'Home', path: '/', icon: <HomeIcon fontSize="small" sx={{ mr: 0.5 }} /> }
      ];
    }

    if (user.role === 'business' || user.role === 'admin') {
      return [
        { title: 'Dashboard', path: '/business/dashboard', icon: <DashboardIcon fontSize="small" sx={{ mr: 0.5 }} /> },
        { title: 'Services', path: '/business/services/new', icon: <AddCircleIcon fontSize="small" sx={{ mr: 0.5 }} /> },
        { title: 'Availability', path: '/business/availability', icon: <AccessTimeIcon fontSize="small" sx={{ mr: 0.5 }} /> }
      ];
    }

    return [
      { title: 'Dashboard', path: '/dashboard', icon: <DashboardIcon fontSize="small" sx={{ mr: 0.5 }} /> },
      { title: 'Book Appointment', path: '/appointments/new', icon: <AddCircleIcon fontSize="small" sx={{ mr: 0.5 }} /> },
      { title: 'Calendar', path: '/calendar', icon: <CalendarMonthIcon fontSize="small" sx={{ mr: 0.5 }} /> }
    ];
  };

  const navLinks = getNavLinks();

  // Check if a link is active
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <HideOnScroll {...props}>
      <AppBar 
        position="sticky" 
        elevation={0}
        sx={{ 
          background: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(10px)',
          boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
          color: 'primary.main',
          mb: 4
        }}
      >
        <Container maxWidth="xl">
          <Toolbar disableGutters>
            {/* Desktop Logo */}
            <Fade in={mounted} timeout={800}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <EventNoteIcon 
                  sx={{ 
                    display: { xs: 'none', md: 'flex' }, 
                    mr: 1,
                    color: 'primary.main',
                    fontSize: '2rem',
                    filter: 'drop-shadow(0px 2px 2px rgba(0, 0, 0, 0.2))'
                  }} 
                />
                <Typography
                  variant="h5"
                  noWrap
                  component={Link}
                  to="/"
                  sx={{
                    mr: 3,
                    display: { xs: 'none', md: 'flex' },
                    fontWeight: 700,
                    background: 'linear-gradient(45deg, #3a6ff7 30%, #5e9cff 90%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    letterSpacing: '.1rem',
                    textDecoration: 'none',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      transform: 'scale(1.05)',
                    }
                  }}
                >
                  SCHEDULER
                </Typography>
              </Box>
            </Fade>

            {/* Mobile Menu */}
            <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleOpenNavMenu}
                color="inherit"
              >
                <MenuIcon />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorElNav}
                anchorOrigin={{
                  vertical: 'bottom',
                  horizontal: 'left',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'left',
                }}
                open={Boolean(anchorElNav)}
                onClose={handleCloseNavMenu}
                sx={{
                  display: { xs: 'block', md: 'none' },
                  '& .MuiPaper-root': {
                    borderRadius: '12px',
                    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',
                  }
                }}
              >
                {navLinks.map((link) => (
                  <MenuItem 
                    key={link.title} 
                    onClick={handleCloseNavMenu} 
                    component={Link} 
                    to={link.path}
                    sx={{
                      backgroundColor: isActive(link.path) ? 'rgba(58, 111, 247, 0.1)' : 'transparent',
                      borderRadius: '8px',
                      mx: 1,
                      my: 0.5
                    }}
                  >
                    {link.icon}
                    <Typography textAlign="center">{link.title}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>

            {/* Mobile Logo */}
            <EventNoteIcon sx={{ display: { xs: 'flex', md: 'none' }, mr: 1, color: 'primary.main' }} />
            <Typography
              variant="h6"
              noWrap
              component={Link}
              to="/"
              sx={{
                mr: 2,
                display: { xs: 'flex', md: 'none' },
                flexGrow: 1,
                fontWeight: 700,
                background: 'linear-gradient(45deg, #3a6ff7 30%, #5e9cff 90%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                letterSpacing: '.1rem',
                textDecoration: 'none',
              }}
            >
              SCHEDULER
            </Typography>

            {/* Desktop Menu */}
            <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
              {navLinks.map((link, index) => (
                <Fade in={mounted} timeout={800 + index * 200}>
                  <Button
                    key={link.title}
                    component={Link}
                    to={link.path}
                    onClick={handleCloseNavMenu}
                    startIcon={link.icon}
                    sx={{ 
                      my: 2, 
                      mx: 1,
                      color: isActive(link.path) ? 'primary.main' : 'text.secondary',
                      fontWeight: isActive(link.path) ? 700 : 500,
                      position: 'relative',
                      '&:after': {
                        content: '""',
                        position: 'absolute',
                        width: isActive(link.path) ? '100%' : '0',
                        height: '3px',
                        bottom: '8px',
                        left: '0',
                        backgroundColor: 'primary.main',
                        borderRadius: '8px',
                        transition: 'width 0.3s ease'
                      },
                      '&:hover': {
                        backgroundColor: 'transparent',
                        '&:after': {
                          width: '100%'
                        }
                      }
                    }}
                  >
                    {link.title}
                  </Button>
                </Fade>
              ))}
            </Box>

            {/* User Menu */}
            <Box sx={{ flexGrow: 0 }}>
              {user ? (
                <Fade in={mounted} timeout={1200}>
                  <Box>
                    <Tooltip title={`${user.name}'s Profile`}>
                      <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                        <Avatar 
                          alt={user.name} 
                          src={`https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`}
                          sx={{ 
                            width: 40, 
                            height: 40,
                            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
                            border: '2px solid white'
                          }}
                        />
                      </IconButton>
                    </Tooltip>
                    <Menu
                      sx={{ 
                        mt: '45px',
                        '& .MuiPaper-root': {
                          borderRadius: '12px',
                          boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',
                        }
                      }}
                      id="menu-appbar"
                      anchorEl={anchorElUser}
                      anchorOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                      }}
                      keepMounted
                      transformOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                      }}
                      open={Boolean(anchorElUser)}
                      onClose={handleCloseUserMenu}
                    >
                      <MenuItem component={Link} to="/profile" onClick={handleCloseUserMenu}>
                        <SettingsIcon fontSize="small" sx={{ mr: 1 }} />
                        <Typography textAlign="center">Profile</Typography>
                      </MenuItem>
                      <MenuItem onClick={handleLogout}>
                        <Typography textAlign="center" color="error">Logout</Typography>
                      </MenuItem>
                    </Menu>
                  </Box>
                </Fade>
              ) : (
                <Fade in={mounted} timeout={1200}>
                  <Box sx={{ display: 'flex' }}>
                    <Button
                      component={Link}
                      to="/login"
                      variant="text"
                      sx={{ 
                        color: 'text.secondary',
                        mr: 1,
                        fontWeight: 600,
                        '&:hover': {
                          color: 'primary.main',
                          backgroundColor: 'transparent'
                        }
                      }}
                    >
                      Login
                    </Button>
                    <Button
                      component={Link}
                      to="/register"
                      variant="contained"
                      className="gradient-primary"
                      sx={{ 
                        fontWeight: 600,
                        boxShadow: '0 4px 14px 0 rgba(58, 111, 247, 0.39)',
                        transition: 'all 0.3s ease',
                        '&:hover': {
                          transform: 'translateY(-2px)',
                          boxShadow: '0 6px 20px 0 rgba(58, 111, 247, 0.5)',
                        }
                      }}
                    >
                      Register
                    </Button>
                  </Box>
                </Fade>
              )}
            </Box>
          </Toolbar>
        </Container>
      </AppBar>
    </HideOnScroll>
  );
};

export default Navbar;
