import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../api/axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import {
  Container,
  Typography,
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Paper,
  Alert,
  CircularProgress,
  FormHelperText,
  Divider,
  Chip,
  Fade,
  useTheme,
  Card,
  CardContent,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  IconButton
} from '@mui/material';
import { toast } from 'react-toastify';
import { motion } from 'framer-motion';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import BusinessIcon from '@mui/icons-material/Business';
import EventNoteIcon from '@mui/icons-material/EventNote';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';

const AppointmentForm = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const theme = useTheme();

  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [businesses, setBusinesses] = useState([]);
  const [services, setServices] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [formData, setFormData] = useState({
    businessId: '',
    serviceId: '',
    date: new Date(),
    startTime: '',
    notes: ''
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const fetchBusinesses = async () => {
      try {
        const res = await api.get('/api/businesses');
        setBusinesses(res.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching businesses:', error);
        toast.error('Failed to load businesses');
        setLoading(false);
      }
    };

    fetchBusinesses();
  }, []);

  useEffect(() => {
    if (formData.businessId) {
      const fetchServices = async () => {
        try {
          const res = await api.get(`/api/services/business/${formData.businessId}`);
          setServices(res.data);
          // Reset service selection when business changes
          setFormData(prev => ({ ...prev, serviceId: '' }));
        } catch (error) {
          console.error('Error fetching services:', error);
          toast.error('Failed to load services for this business');
        }
      };

      fetchServices();
    } else {
      setServices([]);
    }
  }, [formData.businessId]);

  useEffect(() => {
    if (formData.businessId && formData.date) {
      const fetchAvailability = async () => {
        try {
          const dateString = formData.date.toISOString().split('T')[0];
          const res = await api.get(`/api/availability/${formData.businessId}/${dateString}`);
          setAvailableTimes(res.data);
          // Reset time selection when date or business changes
          setFormData(prev => ({ ...prev, startTime: '' }));
        } catch (error) {
          console.error('Error fetching availability:', error);
          toast.error('Failed to load available times');
        }
      };

      fetchAvailability();
    } else {
      setAvailableTimes([]);
    }
  }, [formData.businessId, formData.date]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear errors when field is changed
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleDateChange = (date) => {
    setFormData(prev => ({ ...prev, date }));

    // Clear date error when date is changed
    if (errors.date) {
      setErrors(prev => ({ ...prev, date: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.businessId) newErrors.businessId = 'Please select a business';
    if (!formData.serviceId) newErrors.serviceId = 'Please select a service';
    if (!formData.date) newErrors.date = 'Please select a date';
    if (!formData.startTime) newErrors.startTime = 'Please select a time';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setSubmitting(true);

    try {
      const appointmentData = {
        ...formData,
        date: formData.date.toISOString().split('T')[0]
      };

      const res = await api.post('/api/appointments', appointmentData);
      toast.success('Appointment scheduled successfully!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error creating appointment:', error);
      toast.error(error.response?.data?.message || 'Failed to schedule appointment');
    } finally {
      setSubmitting(false);
    }
  };

  const handleNext = () => {
    let valid = false;

    switch (activeStep) {
      case 0:
        valid = !!formData.businessId;
        if (!valid) setErrors(prev => ({ ...prev, businessId: 'Please select a business' }));
        break;
      case 1:
        valid = !!formData.serviceId;
        if (!valid) setErrors(prev => ({ ...prev, serviceId: 'Please select a service' }));
        break;
      case 2:
        valid = !!formData.date;
        if (!valid) setErrors(prev => ({ ...prev, date: 'Please select a date' }));
        break;
      case 3:
        valid = !!formData.startTime;
        if (!valid) setErrors(prev => ({ ...prev, startTime: 'Please select a time' }));
        break;
      default:
        valid = true;
    }

    if (valid) {
      setActiveStep(prevStep => prevStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep(prevStep => prevStep - 1);
  };

  if (loading) {
    return (
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '60vh' 
      }}>
        <CircularProgress size={60} thickness={4} />
        <Typography variant="body1" sx={{ mt: 2, color: 'text.secondary' }}>
          Loading appointment options...
        </Typography>
      </Box>
    );
  }

  const steps = [
    {
      label: 'Select Business',
      icon: <BusinessIcon />,
      content: (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <FormControl fullWidth error={!!errors.businessId}>
            <InputLabel id="business-select-label">Business</InputLabel>
            <Select
              labelId="business-select-label"
              id="businessId"
              name="businessId"
              value={formData.businessId}
              onChange={handleChange}
              label="Business"
            >
              {businesses.map(business => (
                <MenuItem key={business._id} value={business._id}>
                  {business.name}
                </MenuItem>
              ))}
            </Select>
            {errors.businessId && <FormHelperText>{errors.businessId}</FormHelperText>}
          </FormControl>

          {formData.businessId && businesses.length > 0 && (
            <Box sx={{ mt: 3 }}>
              <Card 
                sx={{ 
                  borderRadius: '12px',
                  boxShadow: '0 8px 24px rgba(0, 0, 0, 0.05)',
                  overflow: 'hidden',
                  transition: 'transform 0.3s ease',
                  '&:hover': {
                    transform: 'translateY(-4px)'
                  }
                }}
                className="card-3d"
              >
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {businesses.find(b => b._id === formData.businessId)?.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {businesses.find(b => b._id === formData.businessId)?.description}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                    <LocationOnIcon sx={{ mr: 1, fontSize: '1rem', color: 'primary.main' }} />
                    <Typography variant="body2">
                      {businesses.find(b => b._id === formData.businessId)?.address}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                    <PhoneIcon sx={{ mr: 1, fontSize: '1rem', color: 'primary.main' }} />
                    <Typography variant="body2">
                      {businesses.find(b => b._id === formData.businessId)?.phone}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Box>
          )}
        </motion.div>
      )
    },
    {
      label: 'Select Service',
      icon: <EventNoteIcon />,
      content: (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <FormControl fullWidth error={!!errors.serviceId}>
            <InputLabel id="service-select-label">Service</InputLabel>
            <Select
              labelId="service-select-label"
              id="serviceId"
              name="serviceId"
              value={formData.serviceId}
              onChange={handleChange}
              label="Service"
              disabled={!formData.businessId}
            >
              {services.map(service => (
                <MenuItem key={service._id} value={service._id}>
                  {service.name} - ${service.price}
                </MenuItem>
              ))}
            </Select>
            {errors.serviceId && <FormHelperText>{errors.serviceId}</FormHelperText>}
          </FormControl>

          {formData.serviceId && services.length > 0 && (
            <Box sx={{ mt: 3 }}>
              <Card 
                sx={{ 
                  borderRadius: '12px',
                  boxShadow: '0 8px 24px rgba(0, 0, 0, 0.05)',
                  overflow: 'hidden',
                  transition: 'transform 0.3s ease',
                  '&:hover': {
                    transform: 'translateY(-4px)'
                  }
                }}
                className="card-3d"
              >
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Typography variant="h6">
                      {services.find(s => s._id === formData.serviceId)?.name}
                    </Typography>
                    <Chip 
                      label={`$${services.find(s => s._id === formData.serviceId)?.price}`}
                      color="primary"
                      sx={{ fontWeight: 'bold' }}
                    />
                  </Box>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                    {services.find(s => s._id === formData.serviceId)?.description}
                  </Typography>
                  <Box sx={{ mt: 2, display: 'flex', alignItems: 'center' }}>
                    <AccessTimeIcon sx={{ mr: 1, fontSize: '1rem', color: 'primary.main' }} />
                    <Typography variant="body2">
                      Duration: {services.find(s => s._id === formData.serviceId)?.duration} minutes
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Box>
          )}
        </motion.div>
      )
    },
    {
      label: 'Select Date',
      icon: <CalendarMonthIcon />,
      content: (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Box sx={{ 
            '& .react-datepicker-wrapper': { width: '100%' },
            '& .react-datepicker': {
              borderRadius: '12px',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
              border: 'none',
              fontFamily: theme.typography.fontFamily
            },
            '& .react-datepicker__header': {
              backgroundColor: theme.palette.primary.main,
              borderBottom: 'none',
              borderRadius: '12px 12px 0 0',
              padding: '10px 0'
            },
            '& .react-datepicker__current-month': {
              color: '#fff',
              fontWeight: 600,
              fontSize: '1rem'
            },
            '& .react-datepicker__day-name': {
              color: 'rgba(255, 255, 255, 0.8)',
              fontWeight: 500
            },
            '& .react-datepicker__day--selected': {
              backgroundColor: theme.palette.primary.main,
              borderRadius: '50%'
            },
            '& .react-datepicker__day:hover': {
              borderRadius: '50%'
            },
            '& .react-datepicker__day--disabled': {
              color: 'rgba(0, 0, 0, 0.3)'
            },
            '& .react-datepicker__navigation': {
              top: '12px'
            }
          }}>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 500 }}>
                Select Date:
              </Typography>
              <Paper
                sx={{
                  p: 2,
                  borderRadius: '12px',
                  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
                  border: errors.date ? `1px solid ${theme.palette.error.main}` : '1px solid rgba(0, 0, 0, 0.1)'
                }}
                className="card-3d"
              >
                <DatePicker
                  selected={formData.date}
                  onChange={handleDateChange}
                  minDate={new Date()}
                  dateFormat="MMMM d, yyyy"
                  className="custom-datepicker"
                  customInput={
                    <TextField
                      fullWidth
                      variant="outlined"
                      InputProps={{
                        startAdornment: (
                          <CalendarMonthIcon sx={{ mr: 1, color: 'primary.main' }} />
                        ),
                      }}
                    />
                  }
                />
              </Paper>
              {errors.date && (
                <FormHelperText error>{errors.date}</FormHelperText>
              )}
            </Box>
          </Box>
        </motion.div>
      )
    },
    {
      label: 'Select Time',
      icon: <AccessTimeIcon />,
      content: (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <FormControl fullWidth error={!!errors.startTime}>
            <InputLabel id="time-select-label">Available Time Slots</InputLabel>
            <Select
              labelId="time-select-label"
              id="startTime"
              name="startTime"
              value={formData.startTime}
              onChange={handleChange}
              label="Available Time Slots"
              disabled={!formData.date || !formData.businessId}
            >
              {availableTimes.map(time => (
                <MenuItem key={time} value={time}>
                  {time}
                </MenuItem>
              ))}
            </Select>
            {errors.startTime && <FormHelperText>{errors.startTime}</FormHelperText>}
          </FormControl>

          {availableTimes.length === 0 && formData.date && formData.businessId && (
            <Alert 
              severity="info" 
              sx={{ 
                mt: 2, 
                borderRadius: '8px',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)'
              }}
            >
              No available time slots for this date. Please select another date.
            </Alert>
          )}

          {formData.startTime && (
            <Box sx={{ mt: 3, textAlign: 'center' }}>
              <Paper 
                sx={{ 
                  p: 3, 
                  borderRadius: '12px',
                  boxShadow: '0 8px 24px rgba(0, 0, 0, 0.05)',
                  background: 'linear-gradient(135deg, rgba(66, 165, 245, 0.05) 0%, rgba(21, 101, 192, 0.05) 100%)'
                }}
                className="card-3d"
              >
                <Typography variant="h6" gutterBottom>
                  Selected Time Slot
                </Typography>
                <Typography variant="h4" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                  {formData.startTime}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  {formData.date.toLocaleDateString(undefined, { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
                </Typography>
              </Paper>
            </Box>
          )}
        </motion.div>
      )
    },
    {
      label: 'Additional Notes',
      icon: <EventNoteIcon />,
      content: (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <TextField
            fullWidth
            id="notes"
            name="notes"
            label="Additional Notes (Optional)"
            multiline
            rows={4}
            value={formData.notes}
            onChange={handleChange}
            placeholder="Any special requests or information for this appointment"
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: '12px'
              }
            }}
          />

          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              Appointment Summary
            </Typography>
            <Paper 
              sx={{ 
                p: 3, 
                mt: 2, 
                borderRadius: '12px',
                boxShadow: '0 8px 24px rgba(0, 0, 0, 0.05)'
              }}
              className="card-3d"
            >
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <BusinessIcon sx={{ mr: 2, color: 'primary.main' }} />
                    <Box>
                      <Typography variant="body2" color="text.secondary">
                        Business
                      </Typography>
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {businesses.find(b => b._id === formData.businessId)?.name}
                      </Typography>
                    </Box>
                  </Box>
                </Grid>

                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <EventNoteIcon sx={{ mr: 2, color: 'primary.main' }} />
                    <Box>
                      <Typography variant="body2" color="text.secondary">
                        Service
                      </Typography>
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {services.find(s => s._id === formData.serviceId)?.name}
                      </Typography>
                    </Box>
                  </Box>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <CalendarMonthIcon sx={{ mr: 2, color: 'primary.main' }} />
                    <Box>
                      <Typography variant="body2" color="text.secondary">
                        Date
                      </Typography>
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {formData.date.toLocaleDateString()}
                      </Typography>
                    </Box>
                  </Box>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <AccessTimeIcon sx={{ mr: 2, color: 'primary.main' }} />
                    <Box>
                      <Typography variant="body2" color="text.secondary">
                        Time
                      </Typography>
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {formData.startTime}
                      </Typography>
                    </Box>
                  </Box>
                </Grid>

                {formData.notes && (
                  <Grid item xs={12}>
                    <Divider sx={{ my: 1 }} />
                    <Typography variant="body2" color="text.secondary">
                      Additional Notes:
                    </Typography>
                    <Typography variant="body2" sx={{ mt: 0.5 }}>
                      {formData.notes}
                    </Typography>
                  </Grid>
                )}

                <Grid item xs={12}>
                  <Divider sx={{ my: 1 }} />
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 1 }}>
                    <Typography variant="subtitle1">
                      Price:
                    </Typography>
                    <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                      ${services.find(s => s._id === formData.serviceId)?.price || '0'}
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </Paper>
          </Box>
        </motion.div>
      )
    }
  ];

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 8 }}>
      <Fade in={true} timeout={800}>
        <Box>
          <motion.div
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <Typography 
              variant="h4" 
              component="h1" 
              gutterBottom
              sx={{ 
                fontWeight: 700,
                background: 'linear-gradient(45deg, #3a6ff7 30%, #5e9cff 90%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 4
              }}
            >
              Book an Appointment
            </Typography>
          </motion.div>

          <Paper 
            sx={{ 
              p: { xs: 2, sm: 4 }, 
              borderRadius: '16px',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.05)',
              overflow: 'hidden'
            }}
            className="card-3d"
          >
            <Stepper 
              activeStep={activeStep} 
              orientation="vertical"
              sx={{
                '& .MuiStepLabel-root': {
                  padding: '8px 0'
                },
                '& .MuiStepIcon-root': {
                  color: 'primary.light',
                  '&.Mui-active': {
                    color: 'primary.main',
                  },
                  '&.Mui-completed': {
                    color: 'success.main',
                  }
                },
                '& .MuiStepConnector-line': {
                  minHeight: '12px'
                }
              }}
            >
              {steps.map((step, index) => (
                <Step key={step.label}>
                  <StepLabel 
                    StepIconComponent={() => (
                      <Box
                        sx={{
                          width: 40,
                          height: 40,
                          borderRadius: '50%',
                          display: 'flex',
                          justifyContent: 'center',
                          alignItems: 'center',
                          backgroundColor: 
                            activeStep > index ? theme.palette.success.main : 
                            activeStep === index ? theme.palette.primary.main : 
                            'rgba(0, 0, 0, 0.08)',
                          color: 
                            activeStep >= index ? '#fff' : 
                            'rgba(0, 0, 0, 0.38)',
                          boxShadow: 
                            activeStep >= index ? '0 4px 8px rgba(0, 0, 0, 0.15)' : 
                            'none',
                          transition: 'all 0.3s ease'
                        }}
                      >
                        {activeStep > index ? <CheckCircleIcon /> : step.icon}
                      </Box>
                    )}
                  >
                    <Typography 
                      variant="subtitle1" 
                      sx={{ 
                        fontWeight: activeStep === index ? 600 : 400,
                        color: activeStep === index ? 'text.primary' : 'text.secondary'
                      }}
                    >
                      {step.label}
                    </Typography>
                  </StepLabel>
                  <StepContent 
                    TransitionProps={{ unmountOnExit: false }}
                    sx={{ pb: 3, px: 2 }}
                  >
                    <Box sx={{ mb: 3, mt: 1 }}>
                      {step.content}
                    </Box>
                    <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, gap: 2, mt: 2 }}>
                      <Button
                        disabled={index === 0}
                        onClick={handleBack}
                        variant="outlined"
                        startIcon={<ArrowBackIcon />}
                        sx={{ 
                          borderRadius: '8px',
                          order: { xs: 2, sm: 1 }
                        }}
                      >
                        Back
                      </Button>
                      <Button
                        variant="contained"
                        onClick={index === steps.length - 1 ? handleSubmit : handleNext}
                        endIcon={index === steps.length - 1 ? null : <ArrowForwardIcon />}
                        disabled={submitting}
                        sx={{ 
                          borderRadius: '8px',
                          order: { xs: 1, sm: 2 },
                          boxShadow: '0 4px 12px rgba(58, 111, 247, 0.2)'
                        }}
                      >
                        {index === steps.length - 1 ? (
                          submitting ? 'Booking...' : 'Book Appointment'
                        ) : (
                          'Continue'
                        )}
                      </Button>
                    </Box>
                  </StepContent>
                </Step>
              ))}
            </Stepper>
          </Paper>
        </Box>
      </Fade>
    </Container>
  );
};

export default AppointmentForm;
