import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { 
  Box, 
  Button, 
  Container, 
  Grid, 
  Typography, 
  Card, 
  CardContent,
  CardMedia,
  Paper,
  useTheme,
  Divider
} from '@mui/material';
import { motion } from 'framer-motion';
import EventAvailableIcon from '@mui/icons-material/EventAvailable';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import BusinessIcon from '@mui/icons-material/Business';
import DevicesIcon from '@mui/icons-material/Devices';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

const MotionBox = motion(Box);
const MotionTypography = motion(Typography);
const MotionButton = motion(Button);
const MotionCard = motion(Card);

const Home = () => {
  const theme = useTheme();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  const cardHoverStyle = {
    transition: 'transform 0.3s, box-shadow 0.3s',
    '&:hover': {
      transform: 'translateY(-8px)',
      boxShadow: '0 12px 28px rgba(0, 0, 0, 0.1)'
    }
  };

  return (
    <Container maxWidth="lg" sx={{ overflow: 'hidden' }}>
      {/* Hero Section */}
      <MotionBox
        sx={{
          pt: 10,
          pb: 8,
          textAlign: 'center',
          position: 'relative'
        }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        {/* Background Elements */}
        <Box
          sx={{
            position: 'absolute',
            top: 20,
            right: -100,
            width: 200,
            height: 200,
            borderRadius: '50%',
            background: 'radial-gradient(circle, rgba(66,165,245,0.1) 0%, rgba(21,101,192,0.05) 70%)',
            zIndex: -1
          }}
        />
        <Box
          sx={{
            position: 'absolute',
            bottom: 50,
            left: -120,
            width: 250,
            height: 250,
            borderRadius: '50%',
            background: 'radial-gradient(circle, rgba(66,165,245,0.08) 0%, rgba(21,101,192,0.02) 70%)',
            zIndex: -1
          }}
        />

        <MotionTypography
          component="h1"
          variant="h2"
          align="center"
          gutterBottom
          initial={{ y: -30, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
          sx={{ 
            fontWeight: 800,
            background: 'linear-gradient(45deg, #3a6ff7 30%, #5e9cff 90%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            mb: 3
          }}
        >
          Appointment Scheduler
        </MotionTypography>

        <MotionTypography 
          variant="h5" 
          align="center" 
          color="text.secondary" 
          paragraph
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.7, delay: 0.2 }}
          sx={{ 
            maxWidth: '800px',
            mx: 'auto',
            lineHeight: 1.6
          }}
        >
          Streamline your appointment booking process. Easy to use for both businesses and customers.
          Book appointments, manage your schedule, and receive reminders all in one place.
        </MotionTypography>

        <MotionBox
          sx={{
            mt: 5,
            display: 'flex',
            justifyContent: 'center',
            gap: 3
          }}
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.7, delay: 0.4 }}
        >
          <MotionButton 
            variant="contained" 
            size="large" 
            component={RouterLink} 
            to="/register"
            endIcon={<ArrowForwardIcon />}
            whileHover={{ 
              scale: 1.05,
              boxShadow: '0 8px 16px rgba(58, 111, 247, 0.3)'
            }}
            whileTap={{ scale: 0.98 }}
            sx={{ 
              borderRadius: '12px',
              px: 4,
              py: 1.5,
              fontSize: '1.1rem',
              fontWeight: 600,
              boxShadow: '0 4px 14px rgba(58, 111, 247, 0.25)'
            }}
          >
            Sign Up Now
          </MotionButton>
          <MotionButton 
            variant="outlined" 
            size="large" 
            component={RouterLink} 
            to="/login"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.98 }}
            sx={{ 
              borderRadius: '12px',
              px: 4,
              py: 1.5,
              fontSize: '1.1rem',
              fontWeight: 600,
              borderWidth: 2
            }}
          >
            Login
          </MotionButton>
        </MotionBox>

        {/* Hero Image */}
        <MotionBox
          sx={{
            mt: 8,
            mb: 4,
            display: 'flex',
            justifyContent: 'center'
          }}
          initial={{ y: 40, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <Paper
            elevation={8}
            sx={{
              width: '90%',
              maxWidth: 900,
              height: 400,
              borderRadius: '16px',
              overflow: 'hidden',
              background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              position: 'relative',
              transform: 'perspective(1000px) rotateX(2deg)',
              transformStyle: 'preserve-3d',
              boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)'
            }}
            className="hero-image"
          >
            <CalendarMonthIcon 
              sx={{ 
                fontSize: 180, 
                color: 'rgba(58, 111, 247, 0.15)',
                position: 'absolute'
              }} 
            />
            <Typography 
              variant="h4" 
              sx={{ 
                position: 'relative', 
                fontWeight: 700,
                color: 'primary.main'
              }}
            >
              Modern Scheduling Solution
            </Typography>
          </Paper>
        </MotionBox>
      </MotionBox>

      {/* Features Section */}
      <Box sx={{ py: 8 }}>
        <MotionTypography
          component="h2"
          variant="h3"
          align="center"
          gutterBottom
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7 }}
          sx={{ 
            mb: 2,
            fontWeight: 700
          }}
        >
          Key Features
        </MotionTypography>

        <MotionBox
          component={motion.div}
          sx={{
            width: '80px',
            height: '4px',
            backgroundColor: 'primary.main',
            mx: 'auto',
            mb: 6,
            borderRadius: '2px'
          }}
          initial={{ width: 0 }}
          whileInView={{ width: '80px' }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, delay: 0.3 }}
        />

        <Grid 
          container 
          spacing={4}
          component={motion.div}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          <Grid item xs={12} md={4} component={motion.div} variants={itemVariants}>
            <MotionCard 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                borderRadius: '16px',
                overflow: 'hidden',
                ...cardHoverStyle
              }}
              className="card-3d"
            >
              <Box 
                sx={{ 
                  p: 4, 
                  display: 'flex', 
                  justifyContent: 'center',
                  background: 'linear-gradient(135deg, rgba(66,165,245,0.1) 0%, rgba(21,101,192,0.05) 100%)'
                }}
              >
                <EventAvailableIcon sx={{ fontSize: 70, color: 'primary.main' }} />
              </Box>
              <CardContent sx={{ flexGrow: 1, p: 3 }}>
                <Typography gutterBottom variant="h5" component="h3" align="center" sx={{ fontWeight: 600, mb: 2 }}>
                  Easy Booking
                </Typography>
                <Typography variant="body1" sx={{ lineHeight: 1.7 }}>
                  Book appointments with just a few clicks. View available time slots and choose what works best for you.
                  Manage your appointments from a simple dashboard.
                </Typography>
              </CardContent>
            </MotionCard>
          </Grid>

          <Grid item xs={12} md={4} component={motion.div} variants={itemVariants}>
            <MotionCard 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                borderRadius: '16px',
                overflow: 'hidden',
                ...cardHoverStyle
              }}
              className="card-3d"
            >
              <Box 
                sx={{ 
                  p: 4, 
                  display: 'flex', 
                  justifyContent: 'center',
                  background: 'linear-gradient(135deg, rgba(66,165,245,0.1) 0%, rgba(21,101,192,0.05) 100%)'
                }}
              >
                <AccessTimeIcon sx={{ fontSize: 70, color: 'primary.main' }} />
              </Box>
              <CardContent sx={{ flexGrow: 1, p: 3 }}>
                <Typography gutterBottom variant="h5" component="h3" align="center" sx={{ fontWeight: 600, mb: 2 }}>
                  Availability Management
                </Typography>
                <Typography variant="body1" sx={{ lineHeight: 1.7 }}>
                  For businesses, easily set your available hours and manage your schedule.
                  Prevent double-bookings and optimize your time with our smart calendar system.
                </Typography>
              </CardContent>
            </MotionCard>
          </Grid>

          <Grid item xs={12} md={4} component={motion.div} variants={itemVariants}>
            <MotionCard 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                borderRadius: '16px',
                overflow: 'hidden',
                ...cardHoverStyle
              }}
              className="card-3d"
            >
              <Box 
                sx={{ 
                  p: 4, 
                  display: 'flex', 
                  justifyContent: 'center',
                  background: 'linear-gradient(135deg, rgba(66,165,245,0.1) 0%, rgba(21,101,192,0.05) 100%)'
                }}
              >
                <NotificationsActiveIcon sx={{ fontSize: 70, color: 'primary.main' }} />
              </Box>
              <CardContent sx={{ flexGrow: 1, p: 3 }}>
                <Typography gutterBottom variant="h5" component="h3" align="center" sx={{ fontWeight: 600, mb: 2 }}>
                  Reminders & Notifications
                </Typography>
                <Typography variant="body1" sx={{ lineHeight: 1.7 }}>
                  Never miss an appointment with automatic email reminders.
                  Get notified about new bookings, cancellations, and schedule changes.
                </Typography>
              </CardContent>
            </MotionCard>
          </Grid>
        </Grid>
      </Box>

      {/* Benefits Section */}
      <Box sx={{ py: 8 }}>
        <MotionTypography
          component="h2"
          variant="h3"
          align="center"
          gutterBottom
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7 }}
          sx={{ 
            mb: 2,
            fontWeight: 700
          }}
        >
          Why Choose Us
        </MotionTypography>

        <MotionBox
          component={motion.div}
          sx={{
            width: '80px',
            height: '4px',
            backgroundColor: 'primary.main',
            mx: 'auto',
            mb: 6,
            borderRadius: '2px'
          }}
          initial={{ width: 0 }}
          whileInView={{ width: '80px' }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, delay: 0.3 }}
        />

        <Grid container spacing={5} alignItems="center">
          <Grid item xs={12} md={6}>
            <MotionBox
              initial={{ x: -50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.7 }}
            >
              <Paper
                elevation={4}
                sx={{
                  p: 4,
                  borderRadius: '16px',
                  height: '100%',
                  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
                  transform: 'perspective(1000px) rotateY(-5deg)',
                  transformStyle: 'preserve-3d',
                  boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)'
                }}
                className="benefits-image"
              >
                <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 300 }}>
                  <BusinessIcon sx={{ fontSize: 150, color: 'rgba(58, 111, 247, 0.2)' }} />
                </Box>
              </Paper>
            </MotionBox>
          </Grid>
          <Grid item xs={12} md={6}>
            <MotionBox
              initial={{ x: 50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.7 }}
            >
              <Typography variant="h4" gutterBottom sx={{ fontWeight: 600, mb: 3 }}>
                For Businesses
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Streamline Your Operations
                </Typography>
                <Typography variant="body1" sx={{ pl: 4, mb: 3 }}>
                  Reduce no-shows and manage your business calendar efficiently with our intuitive scheduling system.
                </Typography>
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Grow Your Customer Base
                </Typography>
                <Typography variant="body1" sx={{ pl: 4, mb: 3 }}>
                  Make it easy for customers to book with you 24/7, increasing your accessibility and reach.
                </Typography>
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Detailed Analytics
                </Typography>
                <Typography variant="body1" sx={{ pl: 4 }}>
                  Track appointment trends, customer preferences, and business performance with our reporting tools.
                </Typography>
              </Box>
            </MotionBox>
          </Grid>
        </Grid>

        <Divider sx={{ my: 8 }} />

        <Grid container spacing={5} alignItems="center" direction={{ xs: 'column-reverse', md: 'row' }}>
          <Grid item xs={12} md={6}>
            <MotionBox
              initial={{ x: -50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.7 }}
            >
              <Typography variant="h4" gutterBottom sx={{ fontWeight: 600, mb: 3 }}>
                For Customers
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Book Anytime, Anywhere
                </Typography>
                <Typography variant="body1" sx={{ pl: 4, mb: 3 }}>
                  Schedule appointments at your convenience, day or night, from any device.
                </Typography>
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Never Miss an Appointment
                </Typography>
                <Typography variant="body1" sx={{ pl: 4, mb: 3 }}>
                  Receive timely reminders and notifications to keep track of your scheduled appointments.
                </Typography>
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, mb: 1, display: 'flex', alignItems: 'center' }}>
                  <Box 
                    component="span" 
                    sx={{ 
                      display: 'inline-block', 
                      width: 8, 
                      height: 8, 
                      borderRadius: '50%', 
                      bgcolor: 'primary.main', 
                      mr: 2 
                    }} 
                  />
                  Manage Your Schedule
                </Typography>
                <Typography variant="body1" sx={{ pl: 4 }}>
                  Easily reschedule or cancel appointments when needed, all from a user-friendly dashboard.
                </Typography>
              </Box>
            </MotionBox>
          </Grid>
          <Grid item xs={12} md={6}>
            <MotionBox
              initial={{ x: 50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.7 }}
            >
              <Paper
                elevation={4}
                sx={{
                  p: 4,
                  borderRadius: '16px',
                  height: '100%',
                  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
                  transform: 'perspective(1000px) rotateY(5deg)',
                  transformStyle: 'preserve-3d',
                  boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)'
                }}
                className="benefits-image"
              >
                <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 300 }}>
                  <DevicesIcon sx={{ fontSize: 150, color: 'rgba(58, 111, 247, 0.2)' }} />
                </Box>
              </Paper>
            </MotionBox>
          </Grid>
        </Grid>
      </Box>

      {/* Call to Action */}
      <MotionBox
        sx={{
          py: 8,
          px: 4,
          mt: 8,
          mb: 6,
          borderRadius: '20px',
          color: 'white',
          textAlign: 'center',
          background: 'linear-gradient(135deg, #3a6ff7 0%, #5e9cff 100%)',
          boxShadow: '0 20px 40px rgba(58, 111, 247, 0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}
        initial={{ y: 50, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8 }}
      >
        {/* Background Elements */}
        <Box
          sx={{
            position: 'absolute',
            top: -50,
            right: -50,
            width: 200,
            height: 200,
            borderRadius: '50%',
            background: 'rgba(255, 255, 255, 0.1)',
            zIndex: 0
          }}
        />
        <Box
          sx={{
            position: 'absolute',
            bottom: -80,
            left: -80,
            width: 250,
            height: 250,
            borderRadius: '50%',
            background: 'rgba(255, 255, 255, 0.08)',
            zIndex: 0
          }}
        />
        
        <Box sx={{ position: 'relative', zIndex: 1 }}>
          <Typography variant="h3" gutterBottom sx={{ fontWeight: 700 }}>
            Ready to streamline your scheduling?
          </Typography>
          <Typography variant="h6" paragraph sx={{ mb: 5, maxWidth: 700, mx: 'auto', opacity: 0.9 }}>
            Join thousands of businesses and customers who save time with our appointment scheduler.
          </Typography>
          <MotionButton 
            variant="contained" 
            size="large" 
            component={RouterLink} 
            to="/register"
            endIcon={<ArrowForwardIcon />}
            whileHover={{ 
              scale: 1.05,
              boxShadow: '0 8px 20px rgba(255, 255, 255, 0.3)'
            }}
            whileTap={{ scale: 0.98 }}
            sx={{ 
              backgroundColor: 'white', 
              color: 'primary.main',
              '&:hover': {
                backgroundColor: 'grey.100',
              },
              borderRadius: '12px',
              px: 5,
              py: 1.5,
              fontSize: '1.1rem',
              fontWeight: 600
            }}
          >
            Get Started Now
          </MotionButton>
        </Box>
      </MotionBox>
    </Container>
  );
};

export default Home;
